export const initialState = {
    basket: [],
    user: null
};

//Selector
export const getBasketTotal = (basket) =>
  basket?.reduce((amount, item) => item.price + amount, 0);

const reducer = (state, action) => { // state is used to hold the current state of the application
    console.log(action);
    switch(action.type){
        case 'ADD_TO_BASKET':
            return {
                ...state, // uses the spread operator (...) to create a new state object that retains the existing properties of the state while modifying specific properties
                basket: [...state.basket, action.item],
            };

        case 'EMPTY_BASKET':
            return {
              ...state,
              basket: []
            };

        case 'REMOVE_FROM_BASKET':
            const index = state.basket.findIndex(
                (basketItem) => basketItem.id === action.id // Find the first one and return 
            );
            let newBasket = [...state.basket];
            if (index >= 0){
                newBasket.splice(index, 1); // go to the index and cut it by one 
            } else{
                console.warn('Cant remove product(id: ${action.id}) as its not in the basket!')
            }

            return {
                ...state, 
                basket: newBasket
            }
        case 'SET_USER':
            return {
                ...state, 
                user: action.user // the user we dispatch
            }

            default:
            return state;
    }
}


export default reducer;
